$(document).ready(function(){var n=$("input[type=range]");n.on("touchend mousedown click",function(){var n=$(this);$(this).val(parseInt(Math.round(n.val())))}),n.on("touchend mousemove click",function(o){if(n.hide().show(0),1===o.which){var i=n.prop("max"),t=$(this),a=t.val()/i*100+"%";$(".range-fill").css("width",a)}})});