$(document).ready(function(){var n=$("input[type=range]");n.on("touchend mousedown click",function(t){var o=$(this),e=parseInt(Math.round(o.val()));if("touchend"===t.type){var a=parseInt(n.attr("max")),i=t.originalEvent.changedTouches[0].clientX,c=n.offset();i-=c.left;var h=Math.round(n.width()/(a+1)),r=Math.floor(i/h);e=r}$(this).val(e)}),n.on("change",function(t){console.log("moved",t),n.hide().show(0)}),n.on("touchend mousemove click",function(t){if(1===t.which||0===t.which){var o=n.attr("max"),e=$(this),a=e.val()/o*100+"%";$(".range-fill").css("width",a)}})});