$(document).ready(function(){var n=$("input[type=range]");n.on("mousedown click",function(){var n=$(this);$(this).val(parseInt(Math.round(n.val())))}),n.on("mousemove click",function(i){if(n.hide().show(0),1===i.which){var o=n.prop("max"),a=$(this),t=a.val()/o*100+"%";$(".range-fill").css("width",t)}})});